definition.script:
def notifyEmail(){
    emailext (
        subject: '$DEFAULT_SUBJECT',
        body: '$DEFAULT_CONTENT',
        recipientProviders: [[$class: 'DevelopersRecipientProvider']],
        replyTo: '$DEFAULT_REPLYTO',
        to: '$DEFAULT_RECIPIENTS'
        )
}
def notifyStart(){
     slackSend channel: '#otus-plataform',
                    color: '#FC0',
                    message: "STARTED: Job '${env.JOB_NAME} [${env.BUILD_NUMBER}]' (${env.BUILD_URL})",
                    teamDomain: 'otussolutions',
                    token: 'OIctpW2cDE8MpIC4uRDfEJpi'
}
pipeline {
  agent any
          tools {
            maven 'maven 3.5.0'
            jdk 'Java8'
          }

  stages {
    stage('Checkout Repository'){
        steps{
            git(url: 'git@github.com:ccem-dev/linda.git',
            credentialsId: '5c08db6f-ba8a-4e32-b867-d90ec54186b4',
            branch: "master")
            notifyStart()
        }
        post {
                failure{
                    notifyEmail()
                }
            }
    }
    stage('Build Code') {
      steps {
        sh 'mvn -f linda-root/pom.xml clean install -Dhttps.protocols=TLSv1,TLSv1.1,TLSv1.2 -DskipTests'
      }
      post {
                failure{
                    notifyEmail()
                }
            }

    }
    stage('Run Unit Tests'){
        steps{

            sh 'mvn -f linda-root/pom.xml test -T 10C '
        }
      post {
        always {
          archive '**/target/surefire-reports/*.xml'
          junit '**/target/surefire-reports/*.xml'
        }
        failure {
            notifyEmail()
        }
      }
    }

    stage('Publish Artifact') {
      steps {
        sh 'mvn -f linda-root/pom.xml deploy -Dhttps.protocols=TLSv1,TLSv1.1,TLSv1.2'
      }
      post {
                failure{
                    notifyEmail()
                }
            }
    }

    stage('Update Docs') {
        tools {
			maven 'maven 3.5.0'
			jdk 'Java8'
		}
        steps {
             withSonarQubeEnv('sonar') {
             sh 'mvn -f linda-root/pom.xml sonar:sonar'
             }
             script {
                 timeout(time: 1, unit: 'HOURS') {
                     def qualityGate = waitForQualityGate()
                     if (qualityGate.status != 'OK') {
                         error "O código não está de acordo com as regras do Sonar: ${qualityGate.status}"
                     }
                 }
             }
        }
        post {
                failure{
                    notifyEmail()
                }
            }
    }
    stage('Deploy') {
      steps {
        echo 'Deploy'
        // sh 'mvn -f otus-root/pom.xml clean install -Ddatabase.host=${DATABASE_DEV_HOST} -Ddatabase.username=${DATABASE_USER} -Ddatabase.password=${DATABASE_PWD}'
      }
      post {
                always{
                    notifyEmail()
                }
            }
    }



    }

  }
