definition.script:
def notifyEmail(){
    emailext (
        subject: '$DEFAULT_SUBJECT',
        body: '$DEFAULT_CONTENT',
        recipientProviders: [[$class: 'DevelopersRecipientProvider']],
        replyTo: '$DEFAULT_REPLYTO',
        to: '$DEFAULT_RECIPIENTS'
        )
}
def notifyStart(){
     slackSend channel: '#otus-plataform',
                    color: '#FC0',
                    message: "STARTED: Job '${env.JOB_NAME} [${env.BUILD_NUMBER}]' (${env.BUILD_URL})",
                    teamDomain: 'otussolutions',
                    token: 'OIctpW2cDE8MpIC4uRDfEJpi'
}
pipeline {
	agent any
		tools {
			maven 'maven 3.5.0'
			jdk 'Java7'
		}

	stages {
	     stage('Checkout Repository'){
            steps{
                // sh "sudo rm rf *"
                git(url: 'git@github.com:ccem-dev/survey-maker.git',
                credentialsId: '5c08db6f-ba8a-4e32-b867-d90ec54186b4',
                branch: "master")
                notifyStart()

            }
            post {
                failure{
                    notifyEmail()
                }
            }
    }
		stage('Build Code') {
			steps {
				sh 'mvn -f survey-root/pom.xml clean install -DskipTests'
			}
			post {
                failure{
                    notifyEmail()
                }
            }
		}

		stage('Run Unit Tests') {
			steps {
				sh 'mvn -f survey-root/pom.xml test'
			}
			post {
                always{
                    notifyEmail()
                }
            }
		}


	}

}
